import logo from './logo.svg';
import { useState } from 'react';
import './App.css';

function App() {

  const title = 'REILib';

  const [content, setContent] = useState('home');

  const handleButtonClick = (page) => {
    setContent(page);
  };

  let pageContent;

  switch (content) {
    case 'home':
      pageContent = (
        <>
          <h1>Welcome to my website!</h1>
          <p>This is some content on my website.</p>
        </>
      );
      break;
    case 'about':
      pageContent = (
        <>
          <h1>About</h1>
          <p>This is the about page of my website.</p>
        </>
      );
      break;
    case 'signin':
      pageContent = (
        <>
          <h1>Sign In</h1>
          <p>Please sign in to access the members-only area of my website.</p>
          <form>
            <label>Email:</label>
            <input type="email" required /><br />
            <label>Password:</label>
            <input type="password" required /><br />
            <button type="submit">Sign In</button>
          </form>
        </>
      );
      break;
    case 'blog':
      pageContent = (
        <>
          <h1>Blog</h1>
          <p>Check out my latest blog posts!</p>
        </>
      );
      break;
    default:
      pageContent = (
        <>
          <h1>Welcome to my website!</h1>
          <p>This is some content on my website.</p>
        </>
      );
      break;
  }

  return (
    <div style={styles.container}>
      <div style={styles.sidebar}>
        <button style={styles.button} onClick={() => handleButtonClick('home')}>Home</button>
        <button style={styles.button} onClick={() => handleButtonClick('about')}>About</button>
        <button style={styles.button} onClick={() => handleButtonClick('signin')}>Sign In</button>
        <button style={styles.button} onClick={() => handleButtonClick('blog')}>Blog</button>
      </div>
      <div style={styles.main}>
        {pageContent}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
    height: '100vh',
  },
  sidebar: {
    backgroundColor: '#f5f5f5',
    width: '200px',
    padding: '20px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
  },
  button: {
    marginBottom: '10px',
    padding: '10px',
    border: 'none',
    backgroundColor: '#e0e0e0',
    color: '#333',
    fontWeight: 'bold',
    cursor: 'pointer',
    width: '100%',
    textAlign: 'left',
  },
  main: {
    flex: 1,
    padding: '20px',
  },
};

export default App;
